mapper.insert:
/* AppAuth.xml -> insertAppAuthInfo */
	INSERT INTO SY_POLICY_L
				(COMP_CD
				,POLICY_CD
				,SYSTEM_CD
				,PROG_CD
				,AUTH
				,STATUS
				,REG_DT
				,REG_ID
				,UPT_DT
				,UPT_ID
				)
		 VALUES (#{COMP_CD}
				,#{POLICY_CD}
				,#{SYSTEM_CD}
				,#{PROG_CD}
				,#{AUTH}
				,#{STATUS}
				,NOW()
				,#{LOGIN_USER}
				,NOW()
				,#{LOGIN_USER}
				)

mapper.update:
/* AppAuth.xml -> updateAppAuthInfo */
	UPDATE SY_POLICY_L
	   SET AUTH = #{AUTH}
		  ,STATUS = #{STATUS}
		  ,UPT_DT = NOW()
		  ,UPT_ID = #{LOGIN_USER}
	 WHERE COMP_CD = #{COMP_CD}
	   AND POLICY_CD = #{POLICY_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}
	   AND PROG_CD = #{PROG_CD}

mapper.delete:
/* AppAuth.xml -> deleteAppAuthInfo */
	DELETE SY_POLICY_L
	 WHERE COMP_CD = #{COMP_CD}
	   AND POLICY_CD = #{POLICY_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}
	   AND PROG_CD = #{PROG_CD}

mapper.select:
/* AppAuth.xml -> selectAppAuthCnt */
	SELECT COUNT(1)
	  FROM SY_POLICY_L  
	 WHERE COMP_CD = #{COMP_CD}
	   AND POLICY_CD = #{POLICY_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}
	   AND PROG_CD = #{PROG_CD}
