mapper.update:
/* CommonCode.xml -> updateCommonCodeMasterInfo */
	UPDATE BC_COMM_H
	   SET HEAD_ID = #{HEAD_ID}
	      ,HEAD_NM = #{HEAD_NM}
	      ,HEAD_EN = #{HEAD_EN}
	      ,HEAD_FR = #{HEAD_FR}
	      ,HEAD_4TH = #{HEAD_4TH}
		  ,REF_CHR1 = #{REF_CHR1}
		  ,REF_CHR2 = #{REF_CHR2}
		  ,REF_CHR3 = #{REF_CHR3}
		  ,REF_CHR4 = #{REF_CHR4}
		  ,REF_CHR5 = #{REF_CHR5}
		  ,REF_NUM1 = #{REF_NUM1}
		  ,REF_NUM2 = #{REF_NUM2}
		  ,REF_NUM3 = #{REF_NUM3}
		  ,REF_NUM4 = #{REF_NUM4}
		  ,REF_NUM5 = #{REF_NUM5}
		  ,REMARKS = #{REMARKS}
		  ,STATUS = #{STATUS}
		  ,UPT_DT = NOW()
		  ,UPT_ID = #{LOGIN_USER}
	 WHERE HEAD_CD = #{HEAD_CD}

mapper.delete:
/* CommonCode.xml -> deleteCommonCodeMasterInfo */
	DELETE BC_COMM_H
 	 WHERE HEAD_CD = #{HEAD_CD}

mapper.select:
/* CommonCode.xml -> selectCommonCodeMasterCnt */
	SELECT COUNT(1)
	  FROM BC_COMM_H   
	 WHERE HEAD_CD = #{HEAD_CD}

/* CommonCode.xml -> selectCommonCodeHead */
	SELECT CONVERT(NVARCHAR, CONVERT(NUMERIC(10, 0), IFNULL(MAX(HEAD_CD), '99900')) + 100) HEAD_CD
  	  FROM BC_COMM_H

