mapper.update:
/* CommonCode.xml -> updateCommonCodeDetailInfo */
	UPDATE BC_COMM_L
	   SET READ_CD = #{READ_CD}
		  ,CODE_NM = #{CODE_NM}
		  ,CODE_EN = #{CODE_EN}
		  ,CODE_FR = #{CODE_FR}
		  ,CODE_4TH = #{CODE_4TH}
		  ,REF_CHR1 = #{REF_CHR1}
		  ,REF_CHR2 = #{REF_CHR2}
		  ,REF_CHR3 = #{REF_CHR3}
		  ,REF_CHR4 = #{REF_CHR4}
		  ,REF_CHR5 = #{REF_CHR5}
		  ,REF_NUM1 = #{REF_NUM1}
		  ,REF_NUM2 = #{REF_NUM2}
		  ,REF_NUM3 = #{REF_NUM3}
		  ,REF_NUM4 = #{REF_NUM4}
		  ,REF_NUM5 = #{REF_NUM5}
		  ,PRIORITY = #{PRIORITY}
		  ,STATUS = SUBSTRING(#{STATUS},1,1)
		  ,UPT_DT = NOW()
		  ,UPT_ID = #{LOGIN_USER}
	 WHERE HEAD_CD = #{HEAD_CD}
	   AND CODE = #{CODE}

mapper.delete:
/* CommonCode.xml -> deleteCommonCodeDetailInfo */
	DELETE BC_COMM_L
	 WHERE HEAD_CD = #{HEAD_CD}

	   AND CODE = #{CODE}

mapper.select:
/* CommonCode.xml -> selectCommonCodeDetailCnt */
	SELECT COUNT(1)
	  FROM BC_COMM_L
	 WHERE HEAD_CD = #{HEAD_CD}
	   AND CODE = #{CODE}

/* CommonCode.xml -> selectCommonCodeList */ SELECT A.HEAD_ID HEAD_ID ,GetHeadNm(A.HEAD_ID, #{languageCode}) HEAD_NM ,GetCodeNm(A.HEAD_ID, B.CODE, #{languageCode}) CODE_NM ,B.READ_CD + ' - ' +GetCodeNm(A.HEAD_ID, B.CODE, #{languageCode}) S_CODE_NM ,B.READ_CD CODE ,B.CODE REFVALUE ,B.REF_CNT CNT ,B.REF_CHR1 REF1 ,B.REF_CHR2 REF2 ,B.REF_CHR3 REF3 FROM BC_COMM_H A INNER JOIN BC_COMM_L B ON B.HEAD_CD = A.HEAD_CD WHERE A.STATUS = 'Y' AND B.STATUS = 'Y' AND A.HEAD_ID IN
#{item}
ORDER BY B.PRIORITY ASC

/* CommonCode.xml -> selectCommPopup */ SELECT H.HEAD_ID, L.READ_CD AS CODE, GetCodeNm(H.HEAD_ID, L.CODE, #{LANG}) AS NAME FROM BC_COMM_H H INNER JOIN BC_COMM_L L ON H.HEAD_CD = L.HEAD_CD AND L.STATUS = 'Y' WHERE H.HEAD_ID = #{S_HEAD_ID}
AND CODE LIKE CONCAT('%', #{RefCode} , '%')
AND (L.CODE LIKE setParam(#{S_CODE})
OR CODE_EN LIKE setParam(#{S_CODE})
OR CODE_FR LIKE setParam(#{S_CODE})
OR CODE_NM LIKE setParam(#{S_CODE})
)
ORDER BY L.PRIORITY
