mapper.select:
/* User.xml -> selectUserInfo */ SELECT ROW_NUMBER() OVER(ORDER BY A.USER_ID) ROWNUM ,A.COMP_CD ,A.USER_ID ,A.NAME_1ST ,A.NAME_2ND ,A.NAME_FULL ,A.USER_TP ,B.CODE_NM USER_TP_NM ,A.USER_GD ,A.DEPT_CD ,CASE WHEN #{LANG} = 'FR' THEN D.DEPT_FR WHEN #{LANG} = 'EN' THEN D.DEPT_EN ELSE D.DEPT_NM END AS DEPT_NM ,A.CUST_CD ,A.WEB_ID ,A.TEL_NO ,CONCAT(A.STR_NM,' ',A.HAUS_NO) AS ADDR ,A.STR_NM ,A.HAUS_NO ,A.ZIP_NO ,A.ORT_NM ,A.REGION_CD ,GETCODENM('LANG_CD', A.LANG_CD, #{LANG}) AS LAMG_NM ,A.LAND_CD ,A.E_MAIL ,A.PW_CURR ,A.PW_PAST1 ,A.PW_PAST2 ,A.PW_PAST3 ,A.WRONG_CNT ,A.LOCK_YN ,A.AREA_CD ,A.AUTH_GR ,A.LANG_CD ,A.CRE_TP ,A.START_DT START_DT ,A.STOP_DT STOP_DT ,A.STATUS ,C.CODE_NM STATUS_NM ,A.REG_DT ,A.REG_ID ,A.UPT_DT ,A.UPT_ID ,A.CHAT_ID FROM BC_USER A INNER JOIN BC_COMM_L B ON A.USER_TP = B.CODE AND B.HEAD_CD = '100200' INNER JOIN BC_COMM_L C ON A.STATUS = C.CODE AND C.HEAD_CD = '100300' LEFT JOIN BC_DEPT D ON A.COMP_CD = D.COMP_CD AND A.DEPT_cD = D.DEPT_CD WHERE 1 = 1 AND USER_TP != '03'
AND A.COMP_CD = #{G_COMP_CD}
AND A.COMP_CD = #{COMP_CD}
AND A.NAME_1ST LIKE CONCAT('%',#{S_USER_NM},'%')
AND A.USER_ID LIKE CONCAT('%',#{S_USER_ID},'%')
AND A.USER_ID = #{USER_ID}
AND A.DEPT_CD = #{DEPT_CD}

mapper.update:
UPDATE BC_USER
   SET NAME_1ST = #{NAME_1ST}
	  ,NAME_2ND = #{NAME_2ND}
	  ,NAME_FULL = CONCAT(#{NAME_1ST}, #{NAME_2ND})
	  ,USER_TP = #{USER_TP}
	  ,USER_GD = #{USER_GD}
	  ,DEPT_CD = #{DEPT_CD}
	  ,CUST_CD = #{CUST_CD}
	  ,WEB_ID = #{WEB_ID}
	  ,STR_NM = #{STR_NM}
	  ,HAUS_NO = #{HAUS_NO}
	  ,ZIP_NO = #{ZIP_NO}
	  ,ORT_NM = #{ORT_NM}
	  ,REGION_CD = #{REGION_CD}
	  ,LAND_CD = #{LAND_CD}
	  ,TEL_NO = #{TEL_NO}
	  ,E_MAIL = #{E_MAIL}
	  ,AREA_CD = #{AREA_CD}
	  ,LANG_CD = #{LANG_CD}
	  ,CRE_TP = #{CRE_TP}

	  ,STATUS = #{STATUS}
	  ,UPT_DT = NOW()
	  ,UPT_ID = #{LOGIN_USER}
	  ,CHAT_ID = #{CHAT_ID}
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

mapper.delete:
DELETE BC_USER
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

mapper.select:
SELECT COUNT(1)
  FROM BC_USER 
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

mapper.update:
UPDATE BC_USER
   SET PW_CURR = #{NEW_PASSWORD}
	  ,PW_PAST1 = PW_CURR
	  ,PW_PAST2 = PW_PAST1
	  ,PW_PAST3 = PW_PAST2

 	  ,LOGIN_DT = NOW()

 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

mapper.select:
SELECT COUNT(1)
  FROM BC_USER 
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}
   AND (PW_CURR = #{NEW_PASSWORD}
    OR PW_PAST1 = #{NEW_PASSWORD}
	OR PW_PAST2 = #{NEW_PASSWORD}
	OR PW_PAST3 = #{NEW_PASSWORD})

UPDATE BC_USER 
   SET LOCK_YN = 'N',
       WRONG_CNT = 0
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}
