mapper.select:
/* Auth.xml -> selectAuthInfo */ SELECT ROW_NUMBER() OVER(ORDER BY A.REG_DT) ROWNUM ,A.COMP_CD ,A.POLICY_CD ,A.POLICY_NM ,A.POLICY_DSC ,A.STATUS ,GETCODENM('USED_OR_NOT', A.STATUS, #{LANG}) AS STATUS_NM FROM SY_POLICY_H A INNER JOIN BC_COMM_L B ON A.STATUS = B.CODE AND B.HEAD_CD = '100000' WHERE A.COMP_CD = #{G_COMP_CD}
AND A.STATUS = #{USED_OR_NOT}
AND A.POLICY_CD LIKE dbo.SetParam(#{S_POLICY_CD})
AND A.POLICY_NM LIKE dbo.SetParam(#{S_POLICY_NM})

mapper.insert:
/* Auth.xml -> insertAuthInfo */
	INSERT INTO SY_POLICY_H
				(COMP_CD
				,POLICY_CD
				,POLICY_NM
				,POLICY_DSC
				,STATUS
				,REG_DT
				,REG_ID
				,UPT_DT
				,UPT_ID
				)
		 VALUES (#{G_COMP_CD}
				,#{POLICY_CD}
				,#{POLICY_NM}
				,#{POLICY_DSC}
				,#{STATUS}
				,NOW()
				,#{LOGIN_USER}
				,NOW()
				,#{LOGIN_USER}
				)

mapper.update:
/* Auth.xml -> updateAuthInfo */
UPDATE SY_POLICY_H
   SET POLICY_NM = #{POLICY_NM}
      ,POLICY_DSC = #{POLICY_DSC}
	  ,STATUS = #{STATUS}
	  ,UPT_DT = NOW()
	  ,UPT_ID = #{LOGIN_USER}
 WHERE COMP_CD = #{COMP_CD}
   AND POLICY_CD = #{POLICY_CD}

mapper.select:
/* Auth.xml -> selectAuthCnt */
	SELECT COUNT(1)
	  FROM SY_POLICY_H 
	 WHERE COMP_CD = #{G_COMP_CD}
	   AND POLICY_CD = #{POLICY_CD}

mapper.delete:
/* Auth.xml -> deleteAuthInfo */
DELETE FROM SY_POLICY_H
   WHERE COMP_CD = #{COMP_CD}
   AND POLICY_CD = #{POLICY_CD}
