mapper.select:

/* Login.xml -> selectCmUserByUserId */
SELECT COUNT(*)
  FROM BC_USER 
 WHERE USER_ID = #{USER_ID}


/* Login.xml -> selectCmUserByUseYn */
SELECT COUNT(*)
  FROM BC_USER 
 WHERE USER_ID = #{USER_ID}
   AND STATUS = 'A'


/* Login.xml -> selectCmUserByAcntLockAt */
SELECT COUNT(*)
  FROM BC_USER 
 WHERE USER_ID = #{USER_ID}
   AND LOCK_YN = 'Y'


/* Login.xml -> selectCmUserForContractReq */
SELECT COUNT(*)
  FROM BC_USER 
 WHERE USER_ID = #{USER_ID}
   AND PW_CURR = #{PASSWORD}
   AND STATUS = 'A'
   AND LOCK_YN = 'N'


/* Login.xml -> selectCmUserForAcntLockAt */
SELECT LOCK_YN
     , WRONG_CNT
  FROM BC_USER 
 WHERE USER_ID = #{USER_ID}


/* Login.xml -> selectCmUserForLoginFailrCo */
SELECT IFNULL(WRONG_CNT, 0)
  FROM BC_USER 
 WHERE USER_ID = #{USER_ID}

mapper.update:

/* Login.xml -> updateCmUserForLoginFailrCo */
UPDATE BC_USER
   SET WRONG_CNT = '0'
 WHERE USER_ID = #{USER_ID}
   AND PW_CURR = #{PASSWORD}

mapper.select:

		/* Login.xml -> updateCmUserForAcntLockAt */
		UPDATE BC_USER
		   SET WRONG_CNT = #{WRONG_CNT}
		     , LOCK_YN = CASE WHEN IFNULL(WRONG_CNT, 0) > 3  THEN 'Y'
                              ELSE 'N'
               END                              
		 WHERE USER_ID = #{USER_ID};

		 COMMIT;

mapper.insert:

/* Login.xml -> insertCmSysConectByLogin */
INSERT INTO SY_LOGIN_HIS
	        (USER_ID
           , LOG_DATE
           , USER_IP
           , USER_OS
           , USERBROWSER
           , ACT_LOG
	        )
	 VALUES (#{USER_ID}
	 	   , NOW()
	       , #{USER_IP}
	       , #{USER_OS}
	       , #{USERBROWSER}
	       , #{STATUS}
		    )

mapper.update:

/* Login.xml -> insertLoginDate */
UPDATE BC_USER 
   SET LOGIN_DT = NOW()
 WHERE USER_ID = #{USER_ID}

mapper.select:

/* Login.xml -> chk_init */
SELECT COUNT(1)
  FROM BC_USER
 WHERE USER_ID =#{USER_ID}
   AND LOGIN_DT IS NOT NULL
