mapper.select:
/* Notice.xml -> selectNoticeInfo */ SELECT ROW_NUMBER() OVER(ORDER BY A.NOTICE_NO DESC) ROWNUM ,A.NOTICE_NO ,A.TITLE ,A.NOTICE_TP ,GetCodeNm('NOTICE_TP', A.NOTICE_TP, #{LANG}) NOTICE_TP_NM ,A.REG_ID ,B.NAME_FULL REG_NM ,FMTDDATE(A.REG_DT, 'YYYY.MM.DD') REG_DT ,A.CNT ,A.CONTENTS ,FMTDDATE(A.FROMDATE, 'YYYY.MM.DD') FROMDATE ,FMTDDATE(A.TODATE, 'YYYY.MM.DD') TODATE ,CASE WHEN DATEDIFF(FMTDDATE(NOW(),'YYYYMMDD'), FMTDDATE(A.FROMDATE,'YYYYMMDD')) <= 0 AND DATEDIFF(FMTDDATE(NOW(),'YYYYMMDD'), FMTDDATE(A.FROMDATE,'YYYYMMDD')) >= -7 THEN 'Y' ELSE 'N' END NEW_YN ,A.FILE_UID FROM BC_NOTICE A INNER JOIN BC_USER B ON A.REG_ID = B.USER_ID WHERE 1 = 1
AND A.TITLE LIKE '%' + SetParam(#{S_TITLE}) + '%'
AND A.CONTENTS LIKE '%' + SetParam(#{S_CONTENT}) + '%'
AND A.NOTICE_NO = #{S_NOTICE_NO}

mapper.insert:
/* Notice.xml -> insertNoticeInfo */
	INSERT INTO BC_NOTICE
				(TITLE
				,NOTICE_TP
				,CONTENTS
				,CNT
				,FROMDATE
				,TODATE

				,FILE_UID

				,STATUS
				,REG_DT
				,REG_ID
				,UPT_DT
				,UPT_ID
				)
		 VALUES (#{TITLE}
				,#{NOTICE_TP}
				,#{CONTENTS}
				,0
				,DATE_FORMAT(#{FROMDATE}, '%Y%m%d')
				,DATE_FORMAT(#{TODATE}, '%Y%m%d') 

				,#{FILE_UID}

				,'A'
				,NOW()
				,#{LOGIN_USER}
				,NOW()
				,#{LOGIN_USER}
				)

mapper.update:
/* Notice.xml -> updateNoticeInfo */
	UPDATE BC_NOTICE
	   SET TITLE = #{TITLE}
	   	  ,NOTICE_TP = #{NOTICE_TP}
	   	  ,CONTENTS = #{CONTENTS}
	   	  ,FROMDATE = DATE_FORMAT(#{FROMDATE}, '%Y%m%d') 
	   	  ,TODATE = DATE_FORMAT(#{TODATE}, '%Y%m%d')
	   	  ,UPT_DT = NOW()
	   	  ,UPT_ID = #{LOGIN_USER}

		  ,FILE_UID = #{FILE_UID}

	 WHERE NOTICE_NO = #{NOTICE_NO}

/* Notice.xml -> updateNoticeCnt */
	UPDATE BC_NOTICE
	   SET CNT = CNT + 1
	 WHERE NOTICE_NO = #{NOTICE_NO}
