mapper.select:
/* System.xml -> selectSystemInfoForMenu */
	SELECT SYSTEM_CD CODE
		  ,CASE WHEN #{LANG} = 'FR' THEN SYSTEM_FR
		        WHEN #{LANG} = 'EN' THEN SYSTEM_EN
		        ELSE SYSTEM_NM END AS VALUE
	  FROM SY_SYSTEM 
	 WHERE COMP_CD = #{G_COMP_CD}

	   AND STATUS = #{S_STATUS}

  ORDER BY PRIORITY

mapper.insert:
/* System.xml -> insertSystemInfo */
	INSERT INTO SY_SYSTEM
				(COMP_CD
				,SYSTEM_CD
				,SYSTEM_NM
				,SYSTEM_EN
				,SYSTEM_FR
				,SYSTEM_4TH
				,PRIORITY
				,STATUS
				,REG_DT
				,REG_ID
				,UPT_DT
				,UPT_ID
				)
		 VALUES (#{G_COMP_CD}
				,#{SYSTEM_CD}
				,#{SYSTEM_NM}
				,#{SYSTEM_EN}
				,#{SYSTEM_FR}
				,#{SYSTEM_4TH}
				,#{PRIORITY}
				,#{STATUS}
				,NOW()
				,#{LOGIN_USER}
				,NOW()
				,#{LOGIN_USER}
				)

mapper.update:
/* System.xml -> updateSystemInfo */
	UPDATE SY_SYSTEM
	   SET SYSTEM_NM = #{SYSTEM_NM}
		  ,SYSTEM_EN = #{SYSTEM_EN}
		  ,SYSTEM_FR = #{SYSTEM_FR}
		  ,SYSTEM_4TH = #{SYSTEM_4TH}
		  ,PRIORITY = #{PRIORITY}
		  ,STATUS = #{STATUS}
		  ,UPT_DT = NOW()
		  ,UPT_ID = #{LOGIN_USER}
	 WHERE COMP_CD = #{G_COMP_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}

mapper.delete:
/* System.xml -> deleteSystemMasterInfo */
	DELETE SY_SYSTEM
	 WHERE COMP_CD = #{COMP_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}

/* System.xml -> deleteProgMasterInfo */
	DELETE SY_PROGRAM
	 WHERE COMP_CD = #{COMP_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}

/* System.xml -> deleteProgDetailInfo */
	DELETE SY_PROGRAM
	 WHERE COMP_CD = #{COMP_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}
	   AND PROG_GROUP = #{PROG_GROUP}

mapper.select:
/* System.xml -> selectSystemCnt */
	SELECT COUNT(1)
	  FROM SY_SYSTEM
	 WHERE COMP_CD = #{G_COMP_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}

/* System.xml -> checkSystemCode */
	SELECT SYSTEM_CD
	  FROM SY_SYSTEM  
	 WHERE COMP_CD = #{G_COMP_CD}
	   AND SYSTEM_CD = #{SYSTEM_CD}

/* System.xml -> getMenuList */ SELECT CASE WHEN #{LANG} = 'FR' THEN SY.SYSTEM_FR WHEN #{LANG} = 'EN' THEN SY.SYSTEM_EN ELSE SY.SYSTEM_EN END PROG_NM3 , CASE WHEN #{LANG} = 'FR' THEN PR2.PROG_FR WHEN #{LANG} = 'EN' THEN PR2.PROG_EN ELSE PR2.PROG_EN END AS PROG_NM2 , CASE WHEN #{LANG} = 'FR' THEN PR1.PROG_FR WHEN #{LANG} = 'EN' THEN PR1.PROG_EN ELSE PR1.PROG_EN END AS PROG_NM1 , PR1.PROG_CD FROM SY_PROGRAM PR1 INNER JOIN SY_PROGRAM PR2 ON PR1.COMP_CD = PR2.COMP_cD AND PR1.PROG_GROUP = PR2.PROG_CD INNER JOIN SY_SYSTEM SY ON PR2.COMP_CD = SY.COMP_CD AND PR2.SYSTEM_CD = SY.SYSTEM_CD WHERE 1 = 1 AND PR1.PROG_URL IS NOT NULL
AND A.PROG_CD = #{S_PROG_CD}
AND PR2.PROG_CD = #{S_PROG_GROUP}
AND SY.SYSTEM_CD = #{S_SYSTEM_CD}
ORDER BY SY.PRIORITY, PR2.PRIORITY, PR1.PRIORITY

/* System.xml -> selectMyMenuData */
	SELECT COUNT(1) 
	  FROM SY_MYMENU  
	 WHERE USER_ID = #{USER_ID}
	   AND PROG_CD = #{PROG_CD}
